mapper.select:
    SELECT * FROM (
	    SELECT t.id, t.token_id, t.image_url, t.type, t.grade, t.description
		FROM token_info t
		         left join token_attribute ta
		                   on t.id = ta.token_info_id
		where 1=1

			and t.type = #{type}


			and t.grade = #{grade}


			and ta.value = #{value}


			and ta.key = #{key}


			and ta.value in 

				#{v}


		group by t.id
	) res

		LIMIT #{limit} OFFSET #{offset}

    select count(*)
	from (
	         SELECT t.id, t.token_id, t.image_url, t.type, t.grade, t.description
	         FROM token_info t
	                  left join token_attribute ta
	                            on t.id = ta.token_info_id
	         where 1=1

				and t.type = #{type}


				and t.grade = #{grade}


				and ta.value = #{value}


				and ta.key = #{key}


				and ta.value in 

					#{v}


	         group by t.id
	) res
